const CACHE_KEY_F2H1 = 'datosGraficoF2H1Cache';
const TIMESTAMP_KEY_F2H1 = 'datosGraficoF2H1Timestamp';
const CACHE_DURATION_MS = 1 * 60 * 1000; //Tiempo en minutos
//----------------------------------------------------------------

//Obtener datos y timestamp del flow context
let cacheData = flow.get(CACHE_KEY_F2H1);
let cacheTimestamp = flow.get(TIMESTAMP_KEY_F2H1);
let now = Date.now();

//verificar si el caché existe y si es valido
if (cacheData && cacheTimestamp && (now - cacheTimestamp < CACHE_DURATION_MS))
{
    //si es True, el cache es valido, y se usa
    msg.payload = cacheData;
    msg.useCache = 1; //Se añade un flag para saber si usamos cache
} else {

    //si el cache no existe o ha expirado, se necesita consultar datos nuevos
    msg.useCache = 0;
    node.log("Cache invalido o no existe. Consultando datos nuevos...");
}

return msg;

