// --- Constantes de Configuración para Detección de Coladas ---
const UMBRAL_POTENCIA_ALTA = 75; // kW - Umbral para considerar que la fusión ha comenzado/continuado
const UMBRAL_POTENCIA_BAJA = 25; // kW - Umbral por debajo del cual se considera baja potencia/posible fin
const TIEMPO_MINIMO_BAJA_POTENCIA_ENTRE_COLADAS_MS = 10 * 60 * 1000; // 10 minutos en milisegundos
const TIEMPO_MINIMO_FUSION_MS = 10 * 60 * 1000; // Opcional: 5 minutos de fusión para ser colada válida

// --- Constantes de Horario Laboral y Offset (copiadas del nodo anterior) ---
const HORA_INICIO_LABORAL_CHILE = 7;
const MINUTOS_INICIO_LABORAL_CHILE = 30;
const HORA_FIN_LABORAL_CHILE = 18;
// const MINUTOS_FIN_LABORAL_CHILE = 0; // No se usa directamente si el fin es HORA_FIN < VALOR
const OFFSET_CHILE_UTC = -4;

// --- Variables de Estado y Contadores ---
let contadorColadasDiaActual = 0;
let estadoHorno = "APAGADO"; // Estados: APAGADO, FUSIONANDO, POSIBLE_FIN_COLADA
let timestampInicioFusion = null;
let timestampInicioBajaPotencia = null;

// --- Determinar Fecha Actual en Chile (para filtrar datos) ---
const ahoraEnChile_DateObj = new Date(Date.now() + OFFSET_CHILE_UTC * 3600000);
const anioActualChile = ahoraEnChile_DateObj.getUTCFullYear();
const mesActualChile = ahoraEnChile_DateObj.getUTCMonth();
const diaActualChile = ahoraEnChile_DateObj.getUTCDate();

// --- Procesamiento de Datos ---
if (msg.payload && Array.isArray(msg.payload)) {
    // Filtrar y ordenar por tiempo primero (si no viene ordenado, crucial)
    // Se asume que msg.payload ya está ordenado por InfluxDB

    msg.payload.forEach(record => {
        if (record === null || typeof record !== 'object' ||
            !record._time || record._field === undefined || record._value === undefined) {
            return; 
        }

        // Considerar solo registros de Potencia para este KPI
        if (record._field !== "Power") {
            return;
        }

        const fechaRecordUtcOriginal = new Date(record._time);
        const recordEnChile_DateObj = new Date(fechaRecordUtcOriginal.getTime() + OFFSET_CHILE_UTC * 3600000);
        const anioRecordChile = recordEnChile_DateObj.getUTCFullYear();
        const mesRecordChile = recordEnChile_DateObj.getUTCMonth();
        const diaRecordChile = recordEnChile_DateObj.getUTCDate();
        const horaRecordChile_Num = recordEnChile_DateObj.getUTCHours();
        const minutosRecordChile_Num = recordEnChile_DateObj.getUTCMinutes();

        // Filtro por día actual y horario laboral
        if (anioRecordChile === anioActualChile &&
            mesRecordChile === mesActualChile &&
            diaRecordChile === diaActualChile) {

            const esDespuesOIgualAlInicioLaboral = 
                (horaRecordChile_Num > HORA_INICIO_LABORAL_CHILE) ||
                (horaRecordChile_Num === HORA_INICIO_LABORAL_CHILE && minutosRecordChile_Num >= MINUTOS_INICIO_LABORAL_CHILE);
            const esAntesDelFinLaboral = (horaRecordChile_Num < HORA_FIN_LABORAL_CHILE);
            const enHorarioLaboral = esDespuesOIgualAlInicioLaboral && esAntesDelFinLaboral;

            if (enHorarioLaboral) {
                const potenciaActual = record._value;
                const tiempoActualMs = fechaRecordUtcOriginal.getTime();

                if (estadoHorno === "APAGADO") {
                    if (potenciaActual >= UMBRAL_POTENCIA_ALTA) {
                        estadoHorno = "FUSIONANDO";
                        timestampInicioFusion = tiempoActualMs;
                        timestampInicioBajaPotencia = null; 
                    }
                } else if (estadoHorno === "FUSIONANDO") {
                    if (potenciaActual < UMBRAL_POTENCIA_BAJA) {
                        estadoHorno = "POSIBLE_FIN_COLADA";
                        timestampInicioBajaPotencia = tiempoActualMs;
                    }
                } else if (estadoHorno === "POSIBLE_FIN_COLADA") {
                    if (potenciaActual >= UMBRAL_POTENCIA_ALTA) { // Subió la potencia de nuevo
                        estadoHorno = "FUSIONANDO"; // Volver a fusionando, fue bajada temporal
                        timestampInicioBajaPotencia = null;
                    } else { // Sigue en baja potencia
                        if (timestampInicioBajaPotencia !== null) {
                            const duracionBajaPotenciaMs = tiempoActualMs - timestampInicioBajaPotencia;
                            if (duracionBajaPotenciaMs >= TIEMPO_MINIMO_BAJA_POTENCIA_ENTRE_COLADAS_MS) {
                                // Condición de tiempo de baja potencia cumplida
                                // Verificar también el tiempo mínimo de fusión (opcional pero recomendado)
                                if (timestampInicioFusion !== null) {
                                    const duracionFusionMs = timestampInicioBajaPotencia - timestampInicioFusion; // Tiempo desde inicio fusión hasta inicio de esta baja larga
                                    if (duracionFusionMs >= TIEMPO_MINIMO_FUSION_MS) {
                                        contadorColadasDiaActual++;
                                    }
                                } else {
                                    // Si no hay timestampInicioFusion pero llegamos aquí, es un estado raro.
                                    // Podría ser que empezó en POSIBLE_FIN_COLADA. Lo seguro es contar si hay fusión previa.
                                }
                                estadoHorno = "APAGADO"; // Resetear para la próxima colada
                                timestampInicioFusion = null; // Importante resetear
                                timestampInicioBajaPotencia = null;
                            }
                        }
                    }
                }
            }
        }
    });
}

// --- Preparar Salida ---
// Puedes añadir este KPI al payload del mensaje original si este nodo sigue a otro,
// o crear un nuevo mensaje. Aquí creo un nuevo objeto en msg.kpiColadas.
if (!msg.kpiColadas) {
    msg.kpiColadas = {};
}
msg.kpiColadas.cantidadColadasDiaActual = contadorColadasDiaActual;
msg.kpiColadas.diaAnalizado = `${String(diaActualChile).padStart(2, '0')}/${String(mesActualChile + 1).padStart(2, '0')}/${anioActualChile}`;
msg.kpiColadas.nota = "Estimación basada en patrones de potencia. Ajustar umbrales según operación real.";
// Puedes también pasar los umbrales usados para referencia
msg.kpiColadas.umbralesConfigurados = {
    UMBRAL_POTENCIA_ALTA_kW: UMBRAL_POTENCIA_ALTA,
    UMBRAL_POTENCIA_BAJA_kW: UMBRAL_POTENCIA_BAJA,
    TIEMPO_MINIMO_BAJA_POTENCIA_ENTRE_COLADAS_min: TIEMPO_MINIMO_BAJA_POTENCIA_ENTRE_COLADAS_MS / (60 * 1000),
    TIEMPO_MINIMO_FUSION_min: TIEMPO_MINIMO_FUSION_MS / (60 * 1000)
};

// Si quieres que este nodo envíe su propio mensaje independiente:
// return { payload: { kpiColadas: msg.kpiColadas } };
// Si quieres añadirlo al mensaje existente y pasarlo:
return msg;

// --- Constantes de Configuración ---
const HORA_INICIO_LABORAL_CHILE = 6;
const MINUTOS_INICIO_LABORAL_CHILE = 0;
const HORA_FIN_LABORAL_CHILE = 18;
const MINUTOS_FIN_LABORAL_CHILE = 0;
const OFFSET_CHILE_UTC = -4;

// --- Determinar Fecha y Hora Actual en Chile ---
const ahoraEnChile_DateObj = new Date(Date.now() + OFFSET_CHILE_UTC * 3600000);
const anioActualChile = ahoraEnChile_DateObj.getUTCFullYear();
const mesActualChile = ahoraEnChile_DateObj.getUTCMonth();
const diaActualChile = ahoraEnChile_DateObj.getUTCDate();
const horaActualChile_Num = ahoraEnChile_DateObj.getUTCHours();
const minutosActualesChile_Num = ahoraEnChile_DateObj.getUTCMinutes();

// --- Contadores y Sets ---
let minutosDeUsoHoyHorarioLaboral = new Set();
let todosLosMinutosConDatosEnergyHoyHorarioLaboral = new Set();
// Variables para KPIs de energía con ACUMULADOR, enfocadas en valores > 0
let primerValorEnergyActivo = { timestampOriginal: null, value: null };
let ultimoValorEnergyActivo = { timestampOriginal: null, value: null };

// --- Procesamiento de Datos ---
if (msg.payload && Array.isArray(msg.payload)) {
    msg.payload.forEach(record => {
        if (record === null || typeof record !== 'object' ||
            !record._time || record._field === undefined || record._value === undefined) {
            return; 
        }

        const fechaRecordUtcOriginal = new Date(record._time);
        const timestampMinutoUtcIso = fechaRecordUtcOriginal.toISOString();

        const recordEnChile_DateObj = new Date(fechaRecordUtcOriginal.getTime() + OFFSET_CHILE_UTC * 3600000);
        const anioRecordChile = recordEnChile_DateObj.getUTCFullYear();
        const mesRecordChile = recordEnChile_DateObj.getUTCMonth();
        const diaRecordChile = recordEnChile_DateObj.getUTCDate();
        const horaRecordChile_Num = recordEnChile_DateObj.getUTCHours();
        const minutosRecordChile_Num = recordEnChile_DateObj.getUTCMinutes();

        if (anioRecordChile === anioActualChile &&
            mesRecordChile === mesActualChile &&
            diaRecordChile === diaActualChile) {

            const esDespuesOIgualAlInicioLaboral = 
                (horaRecordChile_Num > HORA_INICIO_LABORAL_CHILE) ||
                (horaRecordChile_Num === HORA_INICIO_LABORAL_CHILE && minutosRecordChile_Num >= MINUTOS_INICIO_LABORAL_CHILE);
            const esAntesDelFinLaboral = (horaRecordChile_Num < HORA_FIN_LABORAL_CHILE);
            const enHorarioLaboral = esDespuesOIgualAlInicioLaboral && esAntesDelFinLaboral;

            if (enHorarioLaboral) {
                if (record._field === "Energy") {
                    todosLosMinutosConDatosEnergyHoyHorarioLaboral.add(timestampMinutoUtcIso);

                    if (record._value > 350) { // Solo considerar para tiempo de uso y para KPIs de consumo si > 0
                        minutosDeUsoHoyHorarioLaboral.add(timestampMinutoUtcIso);

                        // --- CORRECCIÓN: Capturar primer y último valor del acumulador "Energy" SI ES > 0 ---
                        if (primerValorEnergyActivo.timestampOriginal === null) {
                            primerValorEnergyActivo.timestampOriginal = timestampMinutoUtcIso;
                            primerValorEnergyActivo.value = record._value;
                        }
                        // Siempre actualizar el último valor activo, ya que los datos vienen ordenados por tiempo
                        ultimoValorEnergyActivo.timestampOriginal = timestampMinutoUtcIso;
                        ultimoValorEnergyActivo.value = record._value;
                        // --- FIN CORRECCIÓN ---
                    }
                }
            }
        }
    });
}

// --- Cálculos de Tiempo de Uso/No Uso y Porcentajes ---
const numMinutosDeUsoHoy = minutosDeUsoHoyHorarioLaboral.size;
const inicioLaboralEnMinutosDesdeMedianoche = HORA_INICIO_LABORAL_CHILE * 60 + MINUTOS_INICIO_LABORAL_CHILE;
const finLaboralEnMinutosDesdeMedianoche = HORA_FIN_LABORAL_CHILE * 60 + MINUTOS_FIN_LABORAL_CHILE;
const actualEnMinutosDesdeMedianoche = horaActualChile_Num * 60 + minutosActualesChile_Num;
let minutosReferenciaParaNoUsoHoy = 0;
if (actualEnMinutosDesdeMedianoche >= finLaboralEnMinutosDesdeMedianoche) {
    minutosReferenciaParaNoUsoHoy = finLaboralEnMinutosDesdeMedianoche - inicioLaboralEnMinutosDesdeMedianoche;
} else if (actualEnMinutosDesdeMedianoche > inicioLaboralEnMinutosDesdeMedianoche) {
    minutosReferenciaParaNoUsoHoy = actualEnMinutosDesdeMedianoche - inicioLaboralEnMinutosDesdeMedianoche;
}
minutosReferenciaParaNoUsoHoy = Math.max(0, minutosReferenciaParaNoUsoHoy);
const tiempoDeUsoMinutos = numMinutosDeUsoHoy;
let tiempoDeNoUsoMinutos = minutosReferenciaParaNoUsoHoy - tiempoDeUsoMinutos;
tiempoDeNoUsoMinutos = Math.max(0, tiempoDeNoUsoMinutos);
let porcentajeUso = 0;
let porcentajeNoUso = 0;
if (minutosReferenciaParaNoUsoHoy > 0) {
    porcentajeUso = (tiempoDeUsoMinutos / minutosReferenciaParaNoUsoHoy) * 100;
    porcentajeNoUso = (tiempoDeNoUsoMinutos / minutosReferenciaParaNoUsoHoy) * 100;
}

// --- Calcular KPIs de Consumo Energético para ACUMULADOR (simplificado) ---
let consumoTotalKWh = 0;
let valorInicialParaConsumo = (primerValorEnergyActivo.value !== null) ? primerValorEnergyActivo.value : null;
let valorFinalParaConsumo = (ultimoValorEnergyActivo.value !== null) ? ultimoValorEnergyActivo.value : null;

if (valorInicialParaConsumo !== null && valorFinalParaConsumo !== null) {
    consumoTotalKWh = valorFinalParaConsumo - valorInicialParaConsumo;
    if (consumoTotalKWh < 0) { 
        // Esto puede ocurrir si el contador se resetea o hay datos anómalos.
        // node.warn(`Consumo calculado negativo (${consumoTotalKWh} kWh). Valor inicial: ${valorInicialParaConsumo}, Valor final: ${valorFinalParaConsumo}. Se establecerá a 0.`);
        consumoTotalKWh = 0; 
    }
}

// --- Definir Hora de Inicio y Fin del Periodo ---
const inicioHorarioLaboralHoyStr = `${String(HORA_INICIO_LABORAL_CHILE).padStart(2, '0')}:${String(MINUTOS_INICIO_LABORAL_CHILE).padStart(2, '0')}`;
let finHorarioLaboralConsideradoHoyStr = "";
if (actualEnMinutosDesdeMedianoche >= finLaboralEnMinutosDesdeMedianoche) {
    finHorarioLaboralConsideradoHoyStr = `${String(HORA_FIN_LABORAL_CHILE).padStart(2, '0')}:${String(MINUTOS_FIN_LABORAL_CHILE).padStart(2, '0')}`;
} else if (actualEnMinutosDesdeMedianoche < inicioLaboralEnMinutosDesdeMedianoche) {
    finHorarioLaboralConsideradoHoyStr = `${String(horaActualChile_Num).padStart(2, '0')}:${String(minutosActualesChile_Num).padStart(2, '0')}`;
} else {
    finHorarioLaboralConsideradoHoyStr = `${String(horaActualChile_Num).padStart(2, '0')}:${String(minutosActualesChile_Num).padStart(2, '0')}`;
}

// --- Formatear Resultados ---
msg.payload = {
    tiempoDeUsoHorarioLaboralDiaActual: {
        minutos: tiempoDeUsoMinutos,
        horas: parseFloat((tiempoDeUsoMinutos / 60).toFixed(2)),
        porcentaje: parseFloat(porcentajeUso.toFixed(2))
    },
    tiempoDeNoUsoHorarioLaboralDiaActual: {
        minutos: tiempoDeNoUsoMinutos,
        horas: parseFloat((tiempoDeNoUsoMinutos / 60).toFixed(2)),
        porcentaje: parseFloat(porcentajeNoUso.toFixed(2))
    },
    // --- SECCIÓN DE KPIs ENERGÉTICOS SIMPLIFICADA ---
    kpisConsumoEnergeticoDiaActual: {
        valorInicialAcumuladorKWh: (valorInicialParaConsumo !== null) ? parseFloat(valorInicialParaConsumo.toFixed(3)) : null,
        timestampValorInicialUTC: primerValorEnergyActivo.timestampOriginal,
        valorFinalAcumuladorKWh: (valorFinalParaConsumo !== null) ? parseFloat(valorFinalParaConsumo.toFixed(3)) : null,
        timestampValorFinalUTC: ultimoValorEnergyActivo.timestampOriginal,
        consumoTotalEstimadoKWh: parseFloat(consumoTotalKWh.toFixed(3)),
        // Los tiempos de inicio y fin del horario ya están en contextoCalculo.periodoAnalizadoDiaActual
        notaImportante: "Consumo = (Valor Final Activo - Valor Inicial Activo) del acumulador 'Energy' (kWh). Valores Activos: primer/último registro del día/horario laboral con _value > 0. El _value es el promedio del contador acumulado en su respectivo minuto."
    },
    contextoCalculo: {
        fechaReporte: `${String(diaActualChile).padStart(2, '0')}/${String(mesActualChile + 1).padStart(2, '0')}/${anioActualChile}`,
        horaReporte: `${String(horaActualChile_Num).padStart(2, '0')}:${String(minutosActualesChile_Num).padStart(2, '0')}`,
        periodoAnalizadoDiaActual: { // Estos son el tiempo de inicio y término horario Chile que solicitaste
          inicio: inicioHorarioLaboralHoyStr,
          fin: finHorarioLaboralConsideradoHoyStr
        },
        minutosDelHorarioLaboralHoyConsideradosParaNoUso: minutosReferenciaParaNoUsoHoy,
        offsetUtcUtilizado: OFFSET_CHILE_UTC,
        definicionHorarioLaboral: 
            `${String(HORA_INICIO_LABORAL_CHILE).padStart(2, '0')}:${String(MINUTOS_INICIO_LABORAL_CHILE).padStart(2, '0')} - ` +
            `${String(HORA_FIN_LABORAL_CHILE).padStart(2, '0')}:${String(MINUTOS_FIN_LABORAL_CHILE).padStart(2, '0')} (Hora de Chile)`
    },
    diagnosticoDatosDiaActual: {
        minutosUnicosConAlgunRegistroDeEnergyHoyEnHorarioLaboral: todosLosMinutosConDatosEnergyHoyHorarioLaboral.size,
        minutosUnicosDeUsoDetectadosHoy: numMinutosDeUsoHoy 
    }
};

return msg;


